var ap_instances=new Array();function ap_stopAll(a){for(var b=0;b<ap_instances.length;b++){try{if(ap_instances[b]!=a){document.getElementById("audioplayer"+ap_instances[b].toString()).SetVariable("closePlayer",1)}else{document.getElementById("audioplayer"+ap_instances[b].toString()).SetVariable("closePlayer",0)}}catch(c){}}}function ap_registerPlayers(){var c;var b=document.getElementsByTagName("object");for(var a=0;a<b.length;a++){c=b[a].id;if(c.indexOf("audioplayer")==0){ap_instances[a]=c.substring(11,c.length)}}}var ap_clearID=setInterval(ap_registerPlayers,1000);
